  pool.query("SELECT * FROM dung_cu_the_thao", [], (err, data) => {
    if (err) {
      throw err;
    }
    if (data) {
      return res.status(200).json(data);
    }
  });
};

const createNew = (req, res) => {
  let hinhanh = req.body.hinhanh;
