              [id_dungcuthethao],
              (err, data) => {
                if (err) {
                  throw err;
                }
                if (data) {
                  return res.status(200).json({ message: "success" });
                }
              }
            );
          }
          if (data[0].trang_thai === 1) {
            pool.query(
              "UPDATE dung_cu_the_thao SET trang_thai = 0 WHERE ma_dung_cu_the_thao=?",
              [id_dungcuthethao],
