          if (data[0].trang_thai === 1) {
            pool.query(
              "UPDATE chi_nhanh SET trang_thai = 0 WHERE ma_chi_nhanh=?",
              [id_chinhanh],
              (err, data) => {
                if (err) {
                  throw err;
                }
                if (data) {
                  return res.status(200).json({ message: "success" });
                }
              }
            );
          }
